
public class Desafia_tu_logica_ej1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Escribe un programa en Java que muestre los números del 10 al 1 , en ese orden. Debes utilizar 
		// una estructura de tipo while o do-while
		
		////////////////////////////////////////////////////////
		
		int num ;
		num=10;
		
		while(num>=1) {
			System.out.println("Este es el número " + num);
			num--;
		}
		
		
	}

}
